query:
query getListings($pagination: PaginationInput, $searchTerm: SearchTerm, $parentRecords: [String!], $multiSelectFilters: [MultiSelectFilter], $sortRequest: SortRequest, $multiSKUList: [String]) {
  listings(
    pagination: $pagination
    usecase: MANAGE_INVENTORY_VIEW
    searchTerm: $searchTerm
    parentRecords: $parentRecords
    multiSelectFilters: $multiSelectFilters
    sortRequest: $sortRequest
    multiSKUList: $multiSKUList
  ) {
    listings {
      contributorToken
      id {
        sku
        asin
        fnSku
        productType
        __typename
      }
      offers {
        fulfillmentChannel
        __typename
      }
      inventory {
        available
        unfulfillable
        shippingTemplateName
        shippingTemplateId
        fbaRecommendation {
          type
          parameters {
            key
            value
            __typename
          }
          creationTimestamp
          __typename
        }
        unfulfillableDetails {
          damagedByCarrier
          damagedByCustomer
          damagedByDistributor
          damagedByWarehouse
          defective
          expired
          __typename
        }
        ... on InventoryAFN {
          available
          inbound
          reserved
          reservedDetails {
            customerOrder
            fcTransfer
            fcProcessing
            __typename
          }
          details {
            buyableInTransit
            pendingCustomerOrdersInTransit
            shortTermResearching
            midTermResearching
            longTermResearching
            __typename
          }
          unfulfillable
          isInventoryThresholdReached
          inventoryAlertDisplayText
          __typename
        }
        ... on InventoryMFN {
          available
          unfulfillable
          daysOfSupply
          recommendedInventory
          __typename
        }
        __typename
      }
      performance {
        pageViews
        sales {
          amount
          currencySymbol
          currencyUnit
          __typename
        }
        salesRank {
          productCategory
          rank
          __typename
        }
        unitsSold
        __typename
      }
      incentives
      state
      bundleStatus
      stateDetail {
        stateInlineAction
        stateRedirectionLink
        highlightedState
        parentAsin
        parentSku
        __typename
      }
      lastChanged
      created
      details {
        title
        detailPageUrl
        skuCentralLink
        externalId {
          value
          type
          __typename
        }
        imageUrl
        salesRank {
          rank
          productCategory
          __typename
        }
        type
        numChildren
        customProgram
        __typename
      }
      annotations {
        favorite
        __typename
      }
      offers {
        offerId
        marketplace {
          marketplaceId
          defaultCountryCode
          __typename
        }
        ... on Offer3P {
          fulfillmentChannel
          minAllowedPrice {
            price {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            __typename
          }
          maxAllowedPrice {
            price {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            __typename
          }
          price {
            price {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            shipping {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            __typename
          }
          buyBoxPrice {
            price {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            shipping {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            __typename
          }
          buyBoxPoints
          competitivePrice {
            price {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            shipping {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            __typename
          }
          lowestPrice {
            price {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            shipping {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            __typename
          }
          lowPoints
          businessPrice {
            price {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            shipping {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            percentQuantityPrice {
              quantity
              amount
              __typename
            }
            fixedQuantityPrice {
              quantity
              amount
              __typename
            }
            __typename
          }
          salePrice {
            price {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            shipping {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            __typename
          }
          condition {
            condition
            subCondition
            __typename
          }
          points
          pointsLink
          taxData {
            taxRate
            taxExclusivePrice
            taxExclusiveBusinessPrice
            __typename
          }
          automatedPricing {
            enrollmentStatus
            __typename
          }
          maxRetailPrice {
            price {
              amount
              currencyUnit
              currencySymbol
              __typename
            }
            __typename
          }
          haulOffer {
            label
            state
            isHaulOffer
            hasHaulOffer
            haulPrice {
              price {
                amount
                currencySymbol
                currencyUnit
                __typename
              }
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      marketplaceCount
      actions {
        __typename
        ... on ListingActionWorkflow {
          type
          displayGroup
          solutionId
          solutionParams {
            key
            value
            __typename
          }
          __typename
        }
        ... on ListingActionRedirect {
          type
          displayGroup
          isFetchRequest
          queryParams {
            key
            value
            __typename
          }
          url
          fragmentIdentifier
          __typename
        }
        ... on ListingActionAnnotation {
          type
          displayGroup
          listingAnnotations {
            favorite
            __typename
          }
          __typename
        }
      }
      __typename
    }
    count
    userPreferences {
      name
      sortOrder
      statusFiltering
      fulfillmentFiltering
      lowPriceCondition
      lowPriceFulfillment
      pageRecordSize
      enabledColumns
      enabledFields
      __typename
    }
    configContext {
      merchantId
      marketplaceId
      vatEnabled
      estimatedFeesColumn
      automatedPricingEdit
      automatedPricingView
      similarItemEdit
      similarItemView
      b2b
      defaultLocale
      automatedPricingEnabled
      similarProductPricesEnabled
      competitivePriceDisabled
      showMaxRetailPrice
      showFbaRecommendation
      __typename
    }
    shippingTemplates {
      Legacy
      TemplateName
      TemplateId
      __typename
    }
    __typename
  }
}
